<?phpdate_default_timezone_set("America/Caracas");  $services_json = json_decode(getenv("VCAP_SERVICES") , true);$mongo_config = $services_json["mongodb-1.8"][0]["credentials"];$username = $mongo_config["username"];$password = $mongo_config["password"];$hostname = $mongo_config["hostname"];$port = $mongo_config["port"];$db = $mongo_config["db"];$name = $mongo_config["name"];$connect = "mongodb://${username}:${password}@${hostname}:${port}/${db}";$m = new Mongo($connect);$db = $m->selectDB($db);$modo = "sms";$collection = $db->smsout;if (isset($_GET["limit"]))	{	$limit = $_GET["limit"];	}	else	{	$limit = 10;	}$cursor = $collection->find()->limit($limit);$cursor->sort(array('tiempo' => -1));echo "<table border='1'><tr><th>API</th><th>Numero</th><th>Fecha</th><th>Mensaje</th><th>Status</th><th>Delivery T</th></tr>";foreach($cursor as $obj)	{  echo "<tr>";  echo "<td>" . $obj['api'] . "</td>";   echo "<td>" . $obj['numero'] . "</td>";  echo "<td>" . date('Y-m-d H:i:s', $obj['tiempo']). "</td>";  echo "<td>" . $obj['mensaje'] . "</td>";  echo "<td>" . $obj['status'] . "</td>";  echo "<td>" . date('Y-m-d H:i:s', $obj['dlt']). "</td>";  echo "</tr>";  }echo "</table>";?>